import React from "react";
import { connect } from "react-redux";
import { fetchPosts } from "../actions/index";
import "../css/bootstrap.min.css";

class PostsList extends React.Component {
    componentDidMount () {
        this.props.fetchPosts();
    }
    renderList () {
        return (this.props.posts.map((post) => {
            return(
                <div class="card text-center col-12 col-sm-12 col-md-6 col-lg-6 commentBox normalComment">
                    <div class="card-body">
                        <p class="card-text">
                            <div class = "position-relative d-flex flex-row card-content">
                                    <div><img /></div>
                                    <div class = "headerComment">
                                        <div class = "nameComment">{props.author}<span class = "dateComment">{props.dateAgo}</span></div>
                                        <div class = "descriptionComment">Hola yo soy Daniel muchisimo gusto ojala nos podamos llevar a toda madre</div>
                                    </div>
                            </div>
                        </p>
                    </div>
                </div>
            );
        }));
    }

    render () {
        console.log(this.props.posts);
        return (
            <div>
                PostsList
            </div>
        )     
    } 
};

const mapToStateProps = (state) => {
    return ({posts: state.posts});
}

export default connect(mapToStateProps, { fetchPosts: fetchPosts })(PostsList);
