import React from "react";
import { connect } from "react-redux";
import { fetchPosts } from "../actions/index";
import "../css/bootstrap.min.css";
import "../css/style.css";

class PostsList extends React.Component {
    componentDidMount () {
        this.props.fetchPosts();
    }
    
    renderList () {
        return (this.props.posts.map((post) => {
            console.log(this.props.posts);
            return(
                <div key = {post.title} className="card text-center col-12 col-sm-12 col-md-6 col-lg-6 commentBox normalComment">
                    <div className="card-body">
                        <p className="card-text">
                            <div className = "position-relative d-flex flex-row card-content">
                                    <i className = "person-circle"></i>
                                    <div className = "headerComment">
                                        <div className = "nameComment">{post.title}</div>
                                        <div className = "descriptionComment">{post.description}</div>
                                    </div>
                            </div>
                        </p>
                    </div>
                </div>
            );
        }));
    }

    render () {
        return (
            <div>
                {this.renderList}
            </div>
        )     
    } 
};

const mapToStateProps = (state) => {
    return ({posts: state.posts});
}

export default connect(mapToStateProps, { fetchPosts: fetchPosts })(PostsList);
