import React from "react";
import "../css/bootstrap.min.css";รง
import { connect } from "react-redux";
import { signIn, signOut } from "../actions";

class GoogleAuth extends React.Component {

    state = {isSignedIn: null};

    componentDidMount () {
        window.gapi.load("client:auth2", () => {
            window.gapi.client.init({
                clientId: "534820186827-rpd5rke6m49a2elpn9sa2cmjm364gm9r.apps.googleusercontent.com",
                scope: "email"
            }).then(() => {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.setState({ isSignedIn: this.auth.isSignedIn.get()});
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }

    onAuthChange = (isSignedIn) => {
        if(isSignedIn){
            this.props.signIn();
        } else {
            this.props.signOut();
        }
    };

    signIn = () => {
        this.auth.signIn();
    };

    signOut = () => {
        this.auth.signOut();
    };

    renderAuthButton () {
        if(this.state.isSignedIn === null){
            return null;
        } else if(this.state.isSignedIn){
            return (
                <div>
                    <button onClick = {this.signOut} type = "button" className = "btn btn-primary">
                        Sign out
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <button onClick = {this.signIn} type = "button" className = "btn btn-primary">
                        Sign in
                    </button>
                </div>
            );
        }
    }

    render () {
        return <div>{this.renderAuthButton()}</div>;
    }
};

export default connect(null, {signIn, signOut})(GoogleAuth);
