import React from "react";
import { fetchStreams } from "../../actions";
import { connect } from "react-redux";
import "../../css/bootstrap.min.css";

class StreamList extends React.Component {
    componentDidMount() {
        this.props.fetchStreams();
    }

    renderList () {
        return this.props.streams.map((stream) => {
            return (
                <li key = {stream.description} className = "list-group-item">
                    <div style = {{fontWeight: "bold", fontSize: "25px"}}>{stream.title}</div>
                    
                    {stream.description}
                    {this.renderAdmin.stream}
                </li>
            );
        });
    }

    renderAdmin (stream) {
        if(this.props.currentUserId === stream.userId) {
            return (
                <div class="d-grid gap-2 d-md-flex justify-content-md-end">
                    <button class="btn btn-primary me-md-2" type="button">Edit</button>
                    <button class="btn btn-primary" type="button">Delete</button>
                </div>
            );
        }
    }

    render () {
        return (
            <ul className = "list-group" style = {{marginTop: "20px"}}>
                {this.renderList()}

            </ul>
        );
    }
}

const mapStateToProps = (state) => {
    console.log(state);
    return { 
        streams: Object.values(state.streams),
        currentUserId: state.auth.userId
    };
}

export default connect(mapStateToProps, {fetchStreams})(StreamList);
