import React, { useState } from "react";
import Dropdown from "./Dropdown";
import "../css/bootstrap.min.css";

const options = [
    {
        label: "Afrikaans",
        value: "af"
    },
    {
        label: "Arabic",
        value: "ar"
    },
    {
        label: "Hindi",
        value: "hi"
    }
];

const Translate = () => {
    const [language, setLanguage] = useState(options[1]);


    return(
        <Dropdown 
            options = {options}
            selected = {language}
            onSelectedChange = {setLanguage}
        />
    );
}

export default Dropdown;
