import React from "react";
import "../css/bootstrap.min.css";

const Accordion = (props) => {
    const renderedItems = props.items.map((item) => {
        return(
            <div>
                <a className = "dropdown-item"></a>
                <h3 className>{item.title}</h3>
                <p>{item.content}</p>
            </div>
        );
    });
    
    return (
        <div>
            {renderedItems}
        </div>
    );
}

export default Accordion;
